mapper.select:
select
id, ProjectNum, SFid, DeptName, StageNum, StageRadio,UserName
from dbo.ST_UserStage


        and id = #{id}


        and ProjectNum = #{projectnum}


        and SFid = #{sfid}


        and DeptName = #{deptname}


        and StageNum = #{stagenum}


        and StageRadio = #{stageradio}
